mapper.update:
update  YUNEAR_COPYRIGHTMULTI
	set 
		IS_DELETE = #{IS_DELETE}
	where 
		COPYRIGHTMULTI_ID = #{COPYRIGHTMULTI_ID}

update  YUNEAR_COPYRIGHTMULTI
	set 
		AUDIT_STATUS = #{AUDIT_STATUS}
	where 
		COPYRIGHTMULTI_ID = #{COPYRIGHTMULTI_ID}

mapper.select:
select a.USER_ID, a.MEDIA_NAME, a.KEYWORDS, a.PAY_TYPE, a.DESCR, a.PATH, a.TYPE, a.AUDIT_STATUS, a.PRICE, a.SPREAD_NUM, a.CLICK_NUM, a.SCAN_CODE_NUM, a.DATE_CREATE, a.DATE_MODIFY, a.IS_DELETE, a.COPYRIGHTMULTI_ID from YUNEAR_COPYRIGHTMULTI a where a.USER_ID = #{pd.USER_ID} and a.IS_DELETE = #{pd.IS_DELETE}
and a.KEYWORDS LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%')
and a.AUDIT_STATUS=#{pd.AUDIT_STATUS}
and a.DATE_CREATE >= #{pd.DATE_CREATE}
and a.DATE_CREATE1 <= #{pd.DATE_CREATE1}
